glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec3 a_position;
in vec2 a_uv0;
in vec4 a_color;
out vec2 v_uv0;
out vec4 v_color;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;
  v_color = a_color;
}

glsl3.frag:
precision highp float;
in vec2 v_uv0;
in vec4 v_color;
uniform sampler2D texture;
uniform Properties {
  vec2 center;
  vec2 size;
  float width;
  float height;
};
void main () {
  vec4 color = v_color;
  color *= texture(texture, v_uv0);
  float ratio = size.x / size.y;
  float minX = center.x - (width / 2.0);
  float maxX = center.x + (width / 2.0);
  float minY = center.y - (height * ratio / 2.0);
  float maxY = center.y + (height * ratio / 2.0);
  if (v_uv0.x >= minX && v_uv0.x <= maxX && v_uv0.y >= minY && v_uv0.y <= maxY) {
    discard;
  }
  color.a *= v_color.a;
  gl_FragColor = color;
}

glsl1.vert:
precision highp float;
uniform mat4 cc_matViewProj;
attribute vec3 a_position;
attribute vec2 a_uv0;
attribute vec4 a_color;
varying vec2 v_uv0;
varying vec4 v_color;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;
  v_color = a_color;
}

glsl1.frag:
precision highp float;
varying vec2 v_uv0;
varying vec4 v_color;
uniform sampler2D texture;
uniform vec2 center;
uniform vec2 size;
uniform float width;
uniform float height;
void main () {
  vec4 color = v_color;
  color *= texture2D(texture, v_uv0);
  float ratio = size.x / size.y;
  float minX = center.x - (width / 2.0);
  float maxX = center.x + (width / 2.0);
  float minY = center.y - (height * ratio / 2.0);
  float maxY = center.y + (height * ratio / 2.0);
  if (v_uv0.x >= minX && v_uv0.x <= maxX && v_uv0.y >= minY && v_uv0.y <= maxY) {
    discard;
  }
  color.a *= v_color.a;
  gl_FragColor = color;
}
