glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec3 a_position;
in vec2 a_uv0;
in vec4 a_color;
out vec2 v_uv0;
out vec4 v_color;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;
  v_color = a_color;
}

glsl3.frag:
precision highp float;
in vec2 v_uv0;
in vec4 v_color;
uniform sampler2D texture;
uniform Properties {
  vec2 center;
  vec2 size;
  float width;
  float height;
  float round;
  float feather;
};
void main () {
  vec4 color = v_color;
  color *= texture(texture, v_uv0);
  float x = v_uv0.x;
  float y = v_uv0.y;
  float ratio = size.x / size.y;
  float minX = center.x - (width / 2.0);
  float maxX = center.x + (width / 2.0);
  float minY = center.y - (height * ratio / 2.0);
  float maxY = center.y + (height * ratio / 2.0);
  if (x >= minX && x <= maxX && y >= minY && y <= maxY) {
    if (round == 0.0) discard;
    float roundY = round * ratio;
    vec2 vertex;
    if (x <= minX + round) {
      if (y <= minY + roundY) {
        vertex = vec2(minX + round, (minY + roundY) / ratio);
      } else if (y >= maxY - roundY) {
        vertex = vec2(minX + round, (maxY - roundY) / ratio);
      } else {
        vertex = vec2(minX + round, y / ratio);
      }
    } else if (x >= maxX - round) {
      if (y <= minY + roundY){
        vertex = vec2(maxX - round, (minY + roundY) / ratio);
      } else if (y >= maxY - roundY) {
        vertex = vec2(maxX - round, (maxY - roundY) / ratio);
      } else {
        vertex = vec2(maxX - round, y / ratio);
      }
    } else if (y <= minY + roundY) {
      vertex = vec2(x, (minY + roundY) / ratio);
    } else if (y >= maxY - roundY) {
      vertex = vec2(x, (maxY - roundY) / ratio);
    } else {
      discard;
    }
    float dis = distance(vec2(x, y / ratio), vertex);
    color.a = smoothstep(round - feather, round, dis) * color.a;
  }
  color.a *= v_color.a;
  gl_FragColor = color;
}

